mapper.select:
SELECT 

FROM CMS_SITE a

WHERE a.id = #{id}

SELECT
FROM CMS_SITE a
a.del_flag = #{delFlag}
AND a.name LIKE '%'||#{name}||'%' CONCAT('%', #{name}, '%')
ORDER BY ${page.orderBy}
ORDER BY a.update_time DESC

SELECT 

FROM CMS_SITE a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_time DESC

mapper.update:
UPDATE CMS_SITE SET 
	del_flag = #{delFlag}
WHERE id = #{id}
