mapper.select:
SELECT 

FROM RC_CA_DATA_V2 a

WHERE a.id = #{id}

SELECT 

FROM RC_CA_DATA_V2 a



		AND a.phone_no = #{phoneNo}


		AND a.type = #{type,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


ORDER BY a.id DESC
LIMIT 1

SELECT 

FROM RC_CA_DATA_V2 a



		AND a.phone_no = #{phoneNo}


		AND a.type = #{type,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


		AND a.status = #{status,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


		AND a.create_time >= #{beginTime} 


		AND a.create_time <= #{endTime} 




		ORDER BY ${page.orderBy}


	   ORDER BY a.id DESC

mapper.update:
UPDATE RC_CA_DATA_V2 SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
